<script>

    import Options from './components/Options.svelte';
    import { setLocalData, getLocalData, getAudits, getRGESN } from './modules/module.mjs';

    let defaultVersion = 'v1';
    let referential; // RGESN content
    let index = 0; // Current audit index identifier
    let audits = [ // Audits progression
        {  
            byCriteria: {},
            byCounters: {
                satisfied: 0,
                rejected: 0,
                notApplicable: 0
            },
            selectedVersion: defaultVersion, // By default.
        }
    ];
    let render = false; // Makes Results & AuditForm components reactives

    function importData(file) {
    const reader = new FileReader();		
    reader.onloadend = e => {
        try {
            const data = JSON.parse(e.target.result);
            if(confirm('Attention : cette action entraîne la perte de toutes les données non exportées saisies jusqu\'à présent. Souhaitez-vous poursuivre ?')) {
                audits[index] = data;
                setLocalData('audits', JSON.stringify(audits));
            }
        }
        catch(e) {
            alert('Impossible de lire le fichier.')
        }			
    } 
    reader.readAsText(file);
}

    function exportData() {
        exportFile(JSON.stringify(audits[index]), "application/json", "NumÉcoDiag.json");
    }

    function buildBadge() {
        alert(`Retrouvez votre badge en HTML dans votre dossier téléchargement. Vous pouvez afficher ce badge dans vos communications lorsque 100% des critères sont évalués.`);
        const nbOfCriteria= referential.criteres.length;
        const counters = audits[index].byCounters;
        const assessed = counters.satisfied + counters.rejected + counters.notApplicable;
        const assessedPercent = (assessed/nbOfCriteria * 100).toFixed(0);
        const score = ((counters.satisfied + counters.notApplicable) / nbOfCriteria * 100).toFixed(0);
        const html = `<div class="numecodiag-badge" style="display:flex; align-items: center; gap: 1em; max-width: 60ch; padding: 1em"><img src=" data:image/png;base64," alt="Logo MiNum_Eco"><p>Après auto-évaluation, ce service numérique a un taux de conformité de ${score}% pour ${assessedPercent}% des critères évalués selon le <a href="https://ecoresponsable.numerique.gouv.fr/publications/referentiel-general-ecoconception/" target="_blank">Référentiel Général d'Écoconception de Services Numériques</a> (RGESN). </p></div>`;
        let blob = new Blob([html], {});
        let aElm = document.createElement('a');
        aElm.setAttribute('href', window.URL.createObjectURL(blob));
        aElm.setAttribute('download', 'badge.html');
        aElm.click();
        aElm.remove();
    }

    function changeRGESN(versionToApply) {
        if(versionToApply !== audits[index].selectedVersion) {
            if(resetAudit(versionToApply)) {
                getRGESN(versionToApply);
            }
        }
        else {
            alert('Vous utilisez déjà cette version du référentiel');
        }
    }

    function resetAudit(currentVersion = defaultVersion) {
        if(confirm('Attention : cette action entraîne la perte de toutes les données non exportées saisies jusqu\'à présent. Souhaitez-vous poursuivre ?')) {
            // Udpates runtime values
            audits[index].byCriteria = {};
            audits[index].byCounters.satisfied = 0;
            audits[index].byCounters.rejected = 0;
            audits[index].byCounters.notApplicable = 0;
            audits[index].selectedVersion = currentVersion;
            // Udpates storage
            setLocalData('audits', JSON.stringify(audits));
            // Updates view
            render = !render;
            return true;
        }
        return false
    }

    function exportFile(content, mimetype, filename) {
        let blob = new Blob([content], {type: mimetype});
        let aElm = document.createElement('a');
        aElm.setAttribute('href', window.URL.createObjectURL(blob));
        aElm.setAttribute('download', filename);
        aElm.click();
        aElm.remove();
    }

    function exportResults() {
        let csv = 'ID;Thématique;Libellé du critère;Évaluation;Commentaire;\n';
        for(const criterion of referential.criteres) {
            csv += `${criterion.id};${criterion.thematique};${criterion.critere};`;
            const assessed = audits[index].byCriteria[criterion.id];
            if(assessed !== undefined) {
                switch(assessed.status) {
                    case 'satisfied':
                        csv += 'conforme;';
                            break;
                    case 'rejected':
                        csv += 'non conforme;';
                        break;
                    case 'not-applicable':
                        csv += 'non applicable;';
                        break;
                    default:
                        csv += 'à évaluer;';
                        break;
                }
            }
            else {
                csv += 'à évaluer;';
            }
            if(assessed !== undefined) {
                if(assessed.analysis !== undefined) {
                    csv += `"${assessed.analysis}"`;
                }
                else {
                    csv += ';';
                }
            }
            else {
                csv += ';';
            }
            csv += ';\n';
        }
        exportFile(csv, "text/csv", "NumÉcoDiag.csv");
    }

    getAudits()
        .then(data => audits = JSON.parse(data))
        .catch((warning) => console.warn(warning))
            .finally(() => getRGESN(audits[index].selectedVersion)
                .then((data) => referential = data))
                .catch((error) => console.error(error));
                
</script>

<Options 
    on:changeVersion="{(e) => changeRGESN(e.detail.versionToApply)}"
    on:resetAudit="{() => resetAudit(undefined)}"
    on:buildBadge="{() => buildBadge()}"
    on:exportResults="{exportResults}"
    on:importData="{(e) => importData(e.detail.file)}"
    on:exportData="{exportData}" />